import React, { Component } from 'react'
import api from '../../services/apiClient'
import firebase from '../../services/firebase'

export default class ModalDelete extends Component {
  constructor(props) {
    super(props)
    this.state = {
      text: '',
    }
  }

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    });
  }

  changeInfo = (e) => {
    e.preventDefault()
    const { code, attributeSelected, saveComponentInfoToContext} = this.props
    const newText = this.state.text
    saveComponentInfoToContext(code, attributeSelected, newText)
  }

  render() {
    const { onClose } = this.props
    return (
      <div className='modal-container'>
        <div className='modal-style'>
          <button className='close-modal' onClick={onClose}>
            <img className='close-modal-image' src="../../img/close-icon.png" alt='delete-project'></img>
          </button>
          <form className='form-create-project' onSubmit={this.changeInfo}>
            <input required="required" className='input-modal' type="text"
              name="text"
              placeholder=""
              onChange={this.handleChange} />
            <button className='button-modal' type='submit'>Delete</button>
          </form>
        </div>
      </div>
    )
  }
}
