import React, { Component } from 'react'
import { withData } from '../../contexts/dataContext'
import PropTypes from 'prop-types'
import UserComponentBase from '../droy/UserComponentBase'

class Builder extends Component {
  constructor (props) {
    super(props)
    this.state = {
      userLayoutObj: [],
      mode: 'edit'
    }
  }

  componentDidMount () {
    // db petition
    const { saveComponentInfoToContext, copyUserLayoutObjToContext } = this.props
    const apiInfo = [
      {
        code: 'ClassicHeading1',
        info: { text1: 'Default hello', text2: 'default world' }
      }
    ]
    copyUserLayoutObjToContext(apiInfo)
    this.setState({ userLayoutObj: apiInfo })
  }

  save = () => {
    console.log("Saving info:", this.props.userLayoutObj)
  }

  /* SWITCH MODES */
  switchMode = () => {
    const { mode } = this.state
    this.setState({ mode: mode === 'edit' ? 'view': 'edit' })
  }

  showComponents = () => {
    const { userLayoutObj } = this.state
    return userLayoutObj.map((c) => {
      return <UserComponentBase info={c.info} code={c.code} key={c.code}/>
    })
  }

  render () {
    return (
      <div>
        <h1>Builder page</h1>
        <button onClick={this.save}>Save</button>
        <button onClick={this.switchMode}>Save</button>
        <div id="user-web-page">
          User web page
          {this.showComponents()}
        </div>
      </div>
    )
  }
}

Builder.propTypes = {
  userLayoutObj: PropTypes.array,
  saveInfoToContext: PropTypes.func
}

export default withData(Builder)
