function copyObject (object) {
  return { ...object }
}

function copyArray (array) {
  return [...array]
}

function getComponentById (userLayout, id) {
  return userLayout.find(o => o.code === id)
}
module.exports = {
  copyObject,
  copyArray,
  getComponentById
}
