import React, { Component } from 'react'
import { withData } from '../contexts/dataContext'
import PropTypes from 'prop-types'
import Loading from '../components/droy/Loading'
import '../styles/builder.css'
import api from '../services/apiClient'
import MATCH_COMPONENTS from '../../utils/componentsMatching'
import alias from './utils/alias'


class Builder extends Component {
  constructor(props) {
    super(props)
    this.state = {
      isLoading: true,
      userLayoutObj: [],
      projectStyle: "",
      projectId: "",
      dataError: ""
    }
  }

  /* Updates data context with the actual project information */
  componentDidMount = async () => {
    const { data: { componentsConfiguration, style, _id } } = await api.get(`/projects/${window.location.hostname.split('.')[0]}`)
    this.setState({
      projectId: _id,
      userLayoutObj: componentsConfiguration,
      projectStyle: style,
      isLoading: false
    })
  }

  /* Loops over project info in data context to print user components */
  /* If not components found, prints a pretty message */
  showUserComponents = () => {
    const { userLayoutObj } = this.state

    return userLayoutObj.map(c => {
      const { code, userLayoutObj } = this.props
      const UserComp = MATCH_COMPONENTS[code]
      const { info: componentInfo, componentUserOverrideStyle: userStyle } = alias.findByCode(userLayoutObj, code)
      const componentProps = {}
      componentProps.info = componentInfo
      componentProps.userStyle = userStyle
      return <UserComp {...componentProps} mode='view'/>
      return <UserComp  />
    })
  }

  render() {
    const { isLoading } = this.state
    return (
      <div>
        <div className="main-builder">
          {isLoading && <div className='loading-container'><Loading /></div>}
          {!isLoading && <div className="components-builder">{this.showUserComponents()}</div>}
        </div>
      </div>
    )
  }
}

Builder.propTypes = {
  userLayoutObj: PropTypes.array,
  saveInfoToContext: PropTypes.func
}

export default withData(Builder)
