import React, { Component } from 'react'
import { withData } from '../../../contexts/dataContext'
import PropTypes from 'prop-types'

class ClassicHeading1 extends Component {
  render () {
    const { changeInfo, info } = this.props
    return (
      <div>
        <p onDoubleClick={changeInfo}>{info.text1}</p>
      </div>
    )
  }
}

ClassicHeading1.propTypes = {
  info: PropTypes.object,
  changeInfo: PropTypes.function
}

export default withData(ClassicHeading1)
