import React, { Component } from "react";
import api from "../services/apiClient";
import { withRouter } from "react-router-dom";
import { auth } from '../services/firebase'
import apiClient from "../services/apiClient";

export const AuthContext = React.createContext();

export const withAuth = (Comp) => {
  return class WithAuth extends Component {
    render() {
      return (
        <AuthContext.Consumer>
          {(props) => {
            return (
              <Comp
                handleLogin={props.handleLogin}
                user={props.user}
                signUpError={props.signUpError}
                authLoading={props.authLoading}
                loginError={props.loginError}
                handleSignUp={props.handleSignUp}
                isLoggedIn={props.isLoggedIn}
                handleLogout={props.handleLogout}
                {...this.props}
              />
            );
          }}
        </AuthContext.Consumer>
      );
    }
  };
};

class AuthProvider extends Component {
  constructor(props){
    super(props)
    this.state = {
      isLoggedIn: false,
      user: null,
      authLoading: true,
      loginError: "",
      firebaseUser: ""
    };
  }

  componentDidMount = async () => {

    try {
      const { data: user } = await api.get('/auth/whoami')
      this.setState({
        user,
        isLoggedIn: true,
        authLoading: false
      })
    } catch (error) {
      this.setState({
        user: null,
        isLoggedIn: false,
        authLoading: false
      })
    }
  }

  handleLogin = async ({ email, hashedPassword }) => {
    try {
      const { history } = this.props
      const { data: user } = await api.post('/auth/login', { email, hashedPassword })
      this.setState({
        isLoggedIn: true,
        user,
      })      
      history.push('/')
    } catch (error) {
      this.setState({
        isLoggedIn: false,
        user: null,
        loginError: "Incorrect user or password"
      })
    }
  }

  handleGoogleLogin = async (googleUser) => {
    const { history } = this.props
    var id_token = googleUser.getAuthResponse().id_token;
    const formData = new FormData()
    formData.append('idtoken', id_token)
    const user = await apiClient.post('/auth/tokensignin', formData)
    // var xhr = new XMLHttpRequest();
    // xhr.open('POST', 'http://localhost:3005/auth/tokensignin');
    // xhr.setRequestHeader('Content-Type', 'application/x-www-form-urlencoded');
    // xhr.onload = function() {
      // console.log('Signed in as: ' + xhr.responseText);
    // };
    // xhr.send('idtoken=' + id_token);
    this.setState({
      isLoggedIn: true,
      user,
    })
    history.push('/')
  }

  handleSignUp = async ({ email, hashedPassword, name, confirmationPassword }) => {
    try {
      const { history } = this.props
      if(hashedPassword !== confirmationPassword || hashedPassword.lenght < 6){
        this.setState({ signUpError: 'Incorrect passwords or weak password' })
        return
      }
      const { data: user } = await api.post('/auth/signup', { email, hashedPassword, name })
      this.setState({
        isLoggedIn: true,
        user,
      })
      history.push('/')
    } catch (error) {
      console.log(error)
      this.setState({
        isLoggedIn: false,
        user: null,
        loginError: "Error on signup"
      })
    }
  }

  handleLogout = async () => {
    try {
      const { history } = this.props
      await api.get('/auth/logout')
      this.setState({
        isLoggedIn: false,
        user: null,
      })  
      history.push('/login')
    } catch (error) {
      console.log('Error on logout')      
    }
  };

  render() {
    const { children } = this.props;
    const { isLoggedIn, user, loginError, authLoading, signUpError } = this.state;
    return (
      <AuthContext.Provider
        value={{
          isLoggedIn,
          user,
          authLoading,
          loginError,
          signUpError,
          handleLogin: this.handleLogin,
          handleGoogleLogin: this.handleGoogleLogin,
          handleLogout: this.handleLogout,
          handleSignUp: this.handleSignUp
        }}
      >
        {children}
      </AuthContext.Provider>
    );
  }
}

export default withRouter(AuthProvider);
