import React, { Component } from 'react'
import PropTypes from 'prop-types'
import ImageEditable from '../../droy/ImageEditable'
import LinkEditable from '../../droy/LinkEditable'

const style = {
  backgroundColor: '#1b1b1b',
  padding: '8px 40px 8px 40px',
  display: 'flex',
  color: 'white',
  justifyContent: 'space-between',
  alignItems: 'center',
  height: '60px',
  position: 'relative'
}

const logoContainer = {
  paddingLeft: '50px',
  width: '100px'
}

const textContainer = {
  display: 'flex',
  flexDirection: 'row'
}

const text = {
  paddingRight: '15px',
  fontSize: '0.9rem'
}

class ClassicHeading1 extends Component {

  showLinksNumber = () => {
    const { mode, info, openChangeModal } = this.props
    const allLinks = []
    for (let i = 1; i <= info.linksNumber; i++) {
      allLinks.push(<LinkEditable key={i} mode={mode} info={info.link1} style={text} data-id={`link${i}`} onDoubleClick={openChangeModal}/>)
    }
    return allLinks
  }


  render () {
    const { mode, info, openChangeModal, children: optionsBar, changeImage } = this.props
    console.log(555, info)
    return (
      <div style={style}>
        {optionsBar}
        <ImageEditable style={logoContainer} data-id="logo" src={info.logo.src} changeImage={changeImage}/>
        <div style={textContainer}>
          {this.showLinksNumber()}
        </div>
      </div>
    )
  }
}

ClassicHeading1.propTypes = {
  info: PropTypes.object,
  changeInfo: PropTypes.func,
  optionsBar: PropTypes.object,
  // children: PropTypes.object,
  code: PropTypes.string
}

export default ClassicHeading1
