import React, { Component } from 'react'
import PropTypes from 'prop-types'
import firebase from '../../../services/firebase'
const { uuid } = require('uuidv4');

const style = {
  backgroundColor: '#1b1b1b',
  padding: '8px 40px 8px 40px',
  display: 'flex',
  color: 'white',
  justifyContent: 'space-between',
  alignItems: 'center',
  height: '120px'
}

const logo = {
  width: '10%'
}

const textContainer = {
  display: 'flex',
  flexDirection: 'row'
}

const text = {
  paddingRight: '15px',
  fontSize: '0.9rem'
}

class ClassicHeading1 extends Component {

  state = { selectedFile: null,  image1: 'https://homepages.cae.wisc.edu/~ece533/images/arctichare.png'}


  uploadHandler = () => {
    this.refs.imageUploader.click();
  }


  render () {
    const { info, changeInfo, children: optionsBar, changeImage } = this.props
    return (

      <div style={style}>
        {optionsBar}
        <ImageEditable data-id="image1" src={info.image1} uploadHandler={this.uploadHandler} fileChangedHandler={this.fileChangedHandler}/>
        <div>
          <img style={logo} src='../../../img/logo-white.png' alt='logo-classic-heading'></img>
        </div>
        <div style={textContainer}>
          <p style={text} data-id="text1" onDoubleClick={changeInfo}>{info.text1}</p>
          <p style={text} data-id="text2" onDoubleClick={changeInfo}>{info.text2}</p>
        </div>
      </div>
    )
  }
}

ClassicHeading1.propTypes = {
  info: PropTypes.object,
  changeInfo: PropTypes.func,
  optionsBar: PropTypes.object,
  // children: PropTypes.object,
  code: PropTypes.string
}

export default ClassicHeading1
