import React, { Component } from 'react'

export default class ComponentsSelectorBar extends Component {

  handleAddComponent = (e) => {
     const { addComponent } = this.props
     addComponent(e.target.attributes['data-id'].value)
  }

  render () {
    return (
      <div className="components-bar">
        <div style={{ margin: '20px', backgroundColor: 'yellow', height: '50px', color: 'black' }}>
          <button data-code={'ClassicHeading2'} onClick={this.handleAddComponent}>Add</button>
          Header 1
        </div>
        <div style={{ margin: '20px', backgroundColor: 'pink', height: '200px', color: 'black' }}>Home 1</div>
      </div>
    )
  }
}
