import React, { Component } from "react";

import api from "../services/apiClient";

export const AuthContext = React.createContext();

export const withAuth = (Comp) => {
  return class WithAuth extends Component {
    render() {
      return (
        <AuthContext.Consumer>
          {(props) => {
            return (
              <Comp
                handleLogin={props.handleLogin}
                user={props.user}
                isLoggedIn={props.isLoggedIn}
                handleLogout={props.handleLogout}
                {...this.props}
              />
            );
          }}
        </AuthContext.Consumer>
      );
    }
  };
};

class AuthProvider extends Component {
  constructor(props){
    super(props)
    this.state = {
      isLoggedIn: false,
      user: null,
      isLoading: true,
      authError: ""
    };
  }

  componentDidMount = async () => {
    try {
      const user = await api.get('/auth/whoami')
      this.setState({
        isLoading: false,
        isLoggedIn: true,
        user,
      })

    } catch (error) {
      this.setState({
        isLoading: false,
        isLoggedIn: false,
        user: null,
        authError: error.errors
      })      
    }
  }

  handleLogin = async ({ username, hashedPassword }) => {
    try {
      const { data: user } = await api.get('/auth/login', { username, hashedPassword })
      this.setState({
        isLoggedIn: true,
        user,
      });
    } catch (error) {
      this.setState({
        isLoggedIn: false,
        user: null,
        authError: error
      })      
    }
  };

  handleLogout = async () => {
    try {
      await api.get('/auth/logout')
      this.setState({
        isLoggedIn: false,
        user: null,
      });

    } catch (error) {
      console.log(error);
    }
  };

  render() {
    const { children } = this.props;
    const { isLoggedIn, user } = this.state;
    return (
      <AuthContext.Provider
        value={{
          isLoggedIn,
          user,
          handleLogin: this.handleLogin,
          handleLogout: this.handleLogout,
        }}
      >
        {children}
      </AuthContext.Provider>
    );
  }
}

export default AuthProvider;
