import React, { Component } from 'react'

export default class ImageEditable extends Component {

  constructor(props){
    super(props)
    this.state = { src: "" }
  }

  uploadHandler = (e) => {
    this.refs.imageUploader.click();
  }

  handleError = (e) => {
    e.target.src = '/img/notFound.jpg'
  }

  handleChange = (e) => {
    this.props.src = '/img/loading.gif'
    this.props.changeImage(e)
  }

  render () {
    const { src, changeImage, name } = this.props
    const dataId = this.props['data-id']
    return (
      <div style={{ width: '100%', height: '100%' }}>
        <input onSubmit={this.hs} data-id={dataId} name={name} id={name} style={{ display: 'none' }} onChange={this.handleChange} ref="imageUploader" type="file"/>
        <img onError={this.handleError} onDoubleClick={this.uploadHandler} src={src} alt="" style={{ width: '100%', height: '100%' }}/>
      </div>
    )
  }
}
