import React, { Component } from "react";
import api from "../services/apiClient";
import { withRouter } from "react-router-dom";
import { auth } from '../services/firebase'

export const AuthContext = React.createContext();

export const withAuth = (Comp) => {
  return class WithAuth extends Component {
    render() {
      return (
        <AuthContext.Consumer>
          {(props) => {
            return (
              <Comp
                handleLogin={props.handleLogin}
                user={props.user}
                signUpError={props.signUpError}
                authLoading={props.authLoading}
                loginError={props.loginError}
                handleSignUp={props.handleSignUp}
                isLoggedIn={props.isLoggedIn}
                handleLogout={props.handleLogout}
                {...this.props}
              />
            );
          }}
        </AuthContext.Consumer>
      );
    }
  };
};

class AuthProvider extends Component {
  constructor(props){
    super(props)
    this.state = {
      isLoggedIn: false,
      user: null,
      authLoading: true,
      loginError: "",
      firebaseUser: ""
    };
  }

  componentDidMount = async () => {
    auth.onAuthStateChanged(user => {
      if (user) {
        this.setState({
          user,
          isLoggedIn: true,
          authLoading: false
        })
      } else {
        this.setState({
          user: "",
          isLoggedIn: false,
          authLoading: false
        })
      }
    });
    }

  handleLogin = async ({ email, hashedPassword }) => {
    try {
      const { history } = this.props
      const user = await api.post('/auth/login', { email, hashedPassword })
      this.setState({
        isLoggedIn: true,
        user: user,
      })      
      history.push('/')
    } catch (error) {
      this.setState({
        isLoggedIn: false,
        user: null,
        loginError: "Incorrect user or password"
      })
    }
  }

  handleSignUp = async ({ email, hashedPassword, name, confirmationPassword }) => {
    try {
      const { history } = this.props
      if(hashedPassword !== confirmationPassword || hashedPassword.lenght < 6){
        this.setState({ signUpError: 'Incorrect passwords or weak password' })
        return
      }
      const user = await api.post('/auath/signup', { email, hashedPassword, name })

      this.setState({
        isLoggedIn: true,
        user: auth.currentUser,
      })
      history.push('/')
    } catch (error) {
      console.log(error)
      this.setState({
        isLoggedIn: false,
        user: null,
        loginError: "Error on signup"
      })
    }
  }

  handleLogout = async () => {
    const { history } = this.props
    auth.signOut()
    .then(function() {
      this.setState({
        isLoggedIn: false,
        user: null,
      })  
    })
    .catch(function(error) {
      console.log('in sign out error')
    });
    history.push('/login')
  };

  render() {
    const { children } = this.props;
    const { isLoggedIn, user, loginError, authLoading, signUpError } = this.state;
    return (
      <AuthContext.Provider
        value={{
          isLoggedIn,
          user,
          authLoading,
          loginError,
          signUpError,
          handleLogin: this.handleLogin,
          handleLogout: this.handleLogout,
          handleSignUp: this.handleSignUp
        }}
      >
        {children}
      </AuthContext.Provider>
    );
  }
}

export default withRouter(AuthProvider);
