import React, { Component } from 'react'
import { withData } from '../../contexts/dataContext'

class ComponentsSelectorBar extends Component {

  constructor(props){
    super(props)
    this.state = {}
  }

  componentDidMount = () => {
    const { userLayoutObj } = this.props
    const apiInfo = [
      {
        code: 'ClassicHeading1',
        defaultInfo: {text1: 'Default info classic heading 2'}
      },
      {
        code: 'ClassicHeading2',
        defaultInfo: {text1: 'Default info classic heading 2'}
      }
    ]
    // conseguir todos los componentes del estilo del proyecto, quitando los que ya tiene puestos el usuario
    const userActualComponentsCodes = []
    for (const c of userLayoutObj) userActualComponentsCodes.push(c.code)
    apiInfo.filter(c => !userActualComponentsCodes.includes(c.code) )
    console.log(11111, userLayoutObj)
    this.setState({
      components: apiInfo
    })
  }

  handleAddComponent = (e) => {
     const { addComponent } = this.props
     addComponent(e.target.attributes['data-code'].value)
  }

  render () {
    return (
      <div className="components-bar">

        <div style={{ margin: '20px', backgroundColor: 'yellow', height: '50px', color: 'black' }}>
          <button data-code={'ClassicHeading2'} onClick={this.handleAddComponent}>Add</button>
          Header 1
        </div>
        <div style={{ margin: '20px', backgroundColor: 'pink', height: '200px', color: 'black' }}>Home 1</div>
      </div>
    )
  }
}

export default withData(ComponentsSelectorBar)
