import React, { Component } from 'react'
import NavBar from '../components/droy/NavBar'
import ModalStartPage from '../components/droy/ModalStartProject'
import ModalDelete from '../components/droy/ModalDelete'
import '../styles/homePage.css'
import api from '../services/apiClient'
import { withAuth } from '../contexts/authContext'
import { Link } from "react-router-dom";

const STATUS = {
  LOADING: 'LOADING',
  LOADED: 'LOADED',
  ERROR: 'ERROR',
}

class Homepage extends Component {
  constructor(props) {
    super(props)
    this.state = {
      modal: {type: '', data: ''}, // start, delete
      styles: [],
      allProjects: [],
      status: STATUS.LOADING
    }
  }

  updateComponents = async () => {
    try {
      const stylesApi = await api.get('/styles')
      const projectsApi = await api.get('/projects')
      this.setState({
        styles: stylesApi.data,
        allProjects: projectsApi.data,
        status: STATUS.LOADED,
        modal: {type: '', data: ''}
      })
    } catch (error) {
      this.setState({
        status: STATUS.ERROR
      })
    }
  }

  componentDidMount = async () => {
    this.updateComponents()
  }

  showModal = (e) => {
    const { allProjects } = this.state
    const modalType = e.target.attributes['data-id'].value
    let targetProject = e.target.attributes['target-project']
    if(targetProject){
      console.log(targetProject, allProjects)
      targetProject = allProjects.find(p => p._id === targetProject.value)
    }
    this.setState({
      modal: {
        type: modalType,
        data: targetProject
      }
    })
  }

  closeModal = () => {
    if(this.state.modal.type === "delete") updateComponents()
    this.setState({
      modal: { type: '', data: '' }
    })
  }


  renderProjects = () => {
    const { allProjects } = this.state;
    return allProjects.map((project, index) => {
      return (
        // Hacer componente SquareProject
        <div key={index} >
          <Link to={`builder/${project._id}`}>
            <button className='buttons-homePage'>
              <img className='image-homePage' src="../../img/projects-icon.png" alt='projects'></img>
              <div >
                {project.name}
              </div>
            </button>
          </Link>
          <img onClick={this.showModal} target-project={project._id} data-id="delete" className='image-homePage' src="../../img/delete-icon.png" alt='create-project'></img>
        </div>
      );
    });
  };


  showContent() {
    const { status, styles, modal } = this.state
    switch (status) {
      case STATUS.LOADING:
        return <div> Loading... </div>
      case STATUS.LOADED:
        return (
          <div>
            <h2 className='title-homePage'>Start a new project:</h2>
            <img className='image-homePage' onClick={this.showModal} data-id="start" src="../../img/sum-icon.png" alt='create-project'></img>
            {modal.type === "start" && <ModalStartPage styles={styles} onClose={this.closeModal} />}
            {modal.type === "delete" && <ModalDelete onClose={this.closeModal} project={modal.data} />}
            <h2 className='title-homePage'>Your projects:</h2>
            {this.renderProjects()}
          </div>
        )
      case STATUS.ERROR:
        return <div> Error </div>

      default:
        break;
    }

  }

  render() {
    return (
      <div className='homePage-container'>
        <NavBar />
        {this.showContent()}
      </div>
    )

  }
}

export default withAuth(Homepage)
