import React, { Component } from 'react'
import PropTypes from 'prop-types'
import ImageEditable from '../../droy/ImageEditable'
import LinkEditable from '../../droy/LinkEditable'

const style = {
  backgroundColor: '#1b1b1b',
  padding: '8px 40px 8px 40px',
  display: 'flex',
  color: 'white',
  justifyContent: 'space-between',
  alignItems: 'center',
  height: '60px',
  position: 'relative'
}

const logoContainer = {
  paddingLeft: '50px',
  width: '100px'
}

const textContainer = {
  display: 'flex',
  flexDirection: 'row'
}

const text = {
  paddingRight: '15px',
  fontSize: '0.9rem'
}

class ClassicHeading1 extends Component {

  showLinksNumber = () => {
    [...Array(this.props.linksNumber).keys()].map(i => i * i);
