import React, { Component } from 'react'
import PropTypes from 'prop-types'

const DataContext = React.createContext()

// Consumer Wrapper
export const withData = (Comp) => {
  return class WithData extends Component {
    render () {
      return (
        <DataContext.Consumer>
          { (props) => <Comp {...this.props} {...props} /> }
        </DataContext.Consumer>
      )
    }
  }
}

// Provider def
class DataProvider extends Component {

  constructor(props){
    super(props)
    this.userLayoutObj = []
    this.state = {
      mode: 'edit'
    }
  }

  /* SWITCH MODES */
  switchMode = () => {
    const { mode } = this.state
    this.setState({ mode: mode === 'edit' ? 'view': 'edit' })
  }

  /* COMPONENTS POSITION */
  moveDownComponent = (elementCode) => {
    const { userLayoutConfiguration } = this.state
    const actualConfig = [...userLayoutConfiguration]
    let fromIndex = 0; let element
    for (let i = 0; i < actualConfig.length; i++) {
      const c = actualConfig[i];
      if(c.parentComponentCode === elementCode){
        element = c; fromIndex = i
        break 
      }
    }
    actualConfig.splice(fromIndex, 1)
    actualConfig.splice(fromIndex+1, 0, element)
    this.setState({ userLayoutConfiguration: actualConfig })
  }

  copyUserLayoutObjToContext = (u) => {
    this.userLayoutObj.push(...u)
  }

  saveComponentInfoToContext = (componentInfo, componentCode) => {
    const { userLayoutObj } = this
    const existingComponent = userLayoutObj.filter(c => c.code === componentCode)
    if(existingComponent.length) {
      userLayoutObj.map(c => {
        if(c => c.code === componentCode) Object.assign(c.info, componentInfo)
      })
    } else {
      userLayoutObj.push({ info: componentInfo, code: componentCode })
    }
    console.log(userLayoutObj)
    this.userLayoutObj = userLayoutObj
  };

  render () {
    const { children, code } = this.props
    const { userLayoutObj } = this

    return (
      <DataContext.Provider value={{
        saveComponentInfoToContext: this.saveComponentInfoToContext,
        getUserLayoutObj: this.getUserLayoutObj,
        copyUserLayoutObjToContext: this.copyUserLayoutObjToContext,
        switchMode: this.switchMode,
        userLayoutObj,
        ...this.state

      }}>
        {children}
      </DataContext.Provider>
    )
  }
}

DataProvider.propTypes = {
  children: PropTypes.node
}

export default DataProvider
