import React, { Component } from 'react'
import PropTypes from 'prop-types'

const DataContext = React.createContext()

// Consumer Wrapper
export const withData = (Comp) => {
  return class WithData extends Component {
    render () {
      return (
        <DataContext.Consumer>
          { (props) => <Comp {...this.props} {...props} /> }
        </DataContext.Consumer>
      )
    }
  }
}

// Provider def
class DataProvider extends Component {
  constructor (props) {
    super(props)
    this.state = {
      userLayoutObj: []
    }
  }

  saveComponentInfoToContext = (newComponentInfo) => {
    console.log(11, newComponentInfo)
    const { userLayoutObj } = this.state
    userLayoutObj.filter(c => c.code === newComponentInfo)
    const newInfo = ""
    this.setState({
      userLayoutObj: newInfo
    })
  };

  render () {
    const { children, code } = this.props
    const { userLayoutObj } = this.state
    return (
      <DataContext.Provider value={{
        saveComponentInfoToContext: this.saveComponentInfoToContext,
        userLayoutObj: userLayoutObj,
      }}>
        {children}
      </DataContext.Provider>
    )
  }
}

DataProvider.propTypes = {
  children: PropTypes.node
}

export default DataProvider
