import React, { Component } from 'react'
import { withAuth } from '../contexts/authContext';
import NavBar from '../components/droy/NavBar'
import { Link } from 'react-router-dom'
import '../styles/login-signup.css'
import { GoogleLogin } from 'react-google-login';

class SignUp extends Component {

  constructor(props) {
    super(props)
    this.state = {
      email: "",
      hashedPassword: "",
      name: "",
      confirmationPassword: ""
    }

  }

  handleSubmit = async (e) => {
    e.preventDefault();
    const { email, hashedPassword, name, confirmationPassword } = this.state
    const { history } = this.props
    const user = await firebase.auth().createUserWithEmailAndPassword(email, hashedPassword)
    this.setState({
      email: "",
      password: "",
      hashedPassword: "",
      confirmationPassword: ""
    });
    history.push('/')
  };

  handleSubmitGoogle = () => {
    const { handleGoogle } = this.props;
    handleGoogle();
    this.setState({
      email: "",
      hashedPassword: ""
    });
  };

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  render() {
    const { email, hashedPassword, name, confirmationPassword } = this.state
    const { signUpError } = this.props
    return (
      <div>
        <NavBar />
        <div className='login-signup-container'>
          <div className='logo-container'>
            <img className='login-logo' src='../../../img/logo-white.png' alt='logo-white'></img>
          </div>
          <div className='form-title-container'>
            <h1 className='title-login-signup'>Sign Up</h1>
            <p>{signUpError}</p>
            <form className='login-form' onSubmit={this.handleSubmit}>
              <input className='input-form'
                placeholder="email"
                type="text"
                required="required"
                name="email"
                value={email}
                id="email"
                onChange={this.handleChange}
              />
              <input className='input-form'
                type="password"
                required="required"
                placeholder="······"
                name="hashedPassword"
                id="hashedPassword"
                value={hashedPassword}
                onChange={this.handleChange}
              />
              <input className='input-form'
                type="password"
                required="required"
                placeholder="······"
                name="confirmationPassword"
                id="confirmationPassword"
                value={confirmationPassword}
                onChange={this.handleChange}
              />
              <input className='input-form'
                type="text"
                required="required"
                placeholder="Bob Marley"
                name="name"
                id="name"
                value={name}
                onChange={this.handleChange}
              />
              <input className='button-form' type="submit" value="submit" />
              <img src="/img/google.png"  onClick={this.handleSubmitGoogle}/>
              <Link className='text-form' to="/login">Already have an account? Log in here!</Link>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

export default withAuth(SignUp)
