import React, { Component } from 'react'

export default class LinkEditable extends Component {

  goToLink = () => {
    location.href = this.props.info.href
  }

  render () {
    const { mode, info, style, onDoubleClick } = this.props
    const dataId = this.props['data-id']
    return (
      <div>
        {mode === 'edit'
          ? <button onClick={this.goToLink} data-id={dataId} onDoubleClick={onDoubleClick} style={style}>{info.text}</button>
          : <button data-id={dataId} onDoubleClick={onDoubleClick} style={style} href={info.href}>{info.text}</button>
        }
      </div>
    )
  }
}
