import React, { Component } from 'react'
import { withAuth } from '../contexts/authContext';

class Login extends Component {

  constructor(props){
    super(props)
    this.state = {
      username: "",
      hashedPassword: "",
      error: ""
    };

  }

  handleSubmit = (e) => {
    e.preventDefault();
    const { username, hashedPassword } = this.state;
    const { handleLogin } = this.props;
    handleLogin({ username, hashedPassword });
    this.setState({
      username: "",
      password: "",
    });
  };

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  render() {
    const { username, hashedPassword, error } = this.state
    return (
      <div>
        <h1>Login</h1>
        <p>{error.toString()}</p>
        <form onSubmit={this.handleSubmit}>
          <input
            placeholder="username"
            type="text"
            required="required"
            name="username"
            value={username}
            id="username"
            onChange={this.handleChange}
          />
          <input
            type="password"
            required="required"
            placeholder="······"
            name="hashedPassword"
            id="hashedPassword"
            value={hashedPassword}
            onChange={this.handleChange}
          />
          <input type="submit" value="submit" />
        </form>
      </div>
    );
  }
}

export default withAuth(Login)
