import React, { Component } from 'react'
import PropTypes from 'prop-types'

const DataContext = React.createContext()

// Consumer Wrapper
export const withData = (Comp) => {
  return class WithData extends Component {
    render () {
      return (
        <DataContext.Consumer>
          { (props) => <Comp {...this.props} {...props} /> }
        </DataContext.Consumer>
      )
    }
  }
}

// Provider def
class DataProvider extends Component {

  constructor(props){
    super(props)
    this.state = {
      mode: 'edit',
      userLayoutObj: []
    }
  }

  /* SWITCH MODES */
  switchMode = () => {
    const { mode } = this.state
    this.setState({ mode: mode === 'edit' ? 'view': 'edit' })
  }


  copyUserLayoutObjToContext = (u) => {
    this.setState({
      userLayoutObj: u
    })
  }

  saveComponentInfoToContext = (componentCode, componentAttr, attrContent) => {
    const stateCopy = {...this.state}
    const { newUserLayourObj } = stateCopy

    this.setState({
      userLayoutObj: newUserLayourObj
    })

    const { userLayoutObj } = this
    const existingComponent = userLayoutObj.filter(c => c.code === componentCode)
