import React, { Component } from 'react'
import { withData } from '../../contexts/data'
import MATCH_COMPONENTS from '../../utils/componentsMatching'

class Builder extends Component {
  constructor (props) {
    super(props)
    this.state = {
      userLayoutObj: []
    }
  }

  componentDidMount () {
    // db petition
    const { setInfo } = this.props
    const apiInfo = [
      {
        code: 'ClassicHeading1',
        info: { text1: 'Default hello', text2: 'default world' }
      },
      {
        code: 'ClassicHeading1',
        info: { text1: 'Default hello', text2: 'default world' }
      }
    ]
    setInfo(apiInfo)

    this.setState({ userLayoutObj: apiInfo })
  }

  render () {
    const { userLayoutObj } = this.props
    return (
      <div>
        <h1>Builder page</h1>
        <div id="user-web-page">
          User web page
          {userLayoutObj.map(c => {
            const UserComp = MATCH_COMPONENTS[c.code]
            return <UserComp/>
          })}
        </div>
      </div>
    )
  }
}

Builder.propTypes = {
  userLayoutObj: PropTypes.object
}

export default withData(Builder)
