import { toast } from 'react-toastify'

export function notifyError (msg) {
  toast.error(`⛔ ${msg}`, {
    position: 'top-center',
    autoClose: 4000,
    hideProgressBar: false,
    closeOnClick: true,
    pauseOnHover: true,
    draggable: true,
    progress: undefined
  })
}
