import React, { Component } from 'react'
import PropTypes from 'prop-types'
import { Image, Transformation } from 'cloudinary-react'
import * as firebase from 'firebase'
const { uuid } = require('uuidv4');

const firebaseConfig = {
  apiKey: "AIzaSyBd5EjLbjtvoWmC4jKmFKcTa5Up87I9bTc",
  authDomain: "droy-dev.firebaseapp.com",
  databaseURL: "https://droy-dev.firebaseio.com",
  projectId: "droy-dev",
  storageBucket: "droy-dev.appspot.com",
  messagingSenderId: "832956384747",
  appId: "1:832956384747:web:3870c2e89e40d7401385ae"
};
firebase.initializeApp(firebaseConfig);
var storage = firebase.storage().ref();


const style = {
  height: '200px',
  backgroundColor: 'tomato'
}

class ClassicHeading1 extends Component {

  state = { selectedFile: null,  }

  fileChangedHandler = e => {
    console.log(firebase)
    const file = e.target.files[0]
    if(size > 10000) return
    console.log(file)
    return
    const storageRef = firebase.storage().ref(`/dsadsadsa/${uuid()}`)
    const task = storageRef.put(file)
    task.on('state_changed', (snapshot) => {
      let percentage = (snapshot.bytesTransferred / snapshot.totalBytes) * 100
      console.log(percentage)
    }, (error) => {
      console.error(error.message)
    }, () => {
      task.snapshot.ref.getDownloadURL().then(function(downloadURL) {
        console.log('File available at', downloadURL);
      });
    })

  }

  uploadHandler = () => {
    this.refs.imageUploader.click();
  }

  handleInputChange(e) {
    /* this.setState({
      [e.target.name]: e.target.value
    });*/
  }


  /*  uploadHandler = () => {
    console.log(111)
    axios.post('https://api.cloudinary.com/v1_1/dm99dkmlr/upload', this.state.selectedFile, {
      onUploadProgress: progressEvent => {
        console.log(progressEvent.loaded / progressEvent.total)
      }
    })
  } */

  render () {
    const { info, changeInfo, children: optionsBar } = this.props
    return (

      <div style={style}>
        { optionsBar }
        <input onChange={this.fileChangedHandler} ref="imageUploader" type="file" name="image" id="image"/>

        <Image
          dpr="auto"
          onDoubleClick={this.uploadHandler}
          responsive
          responsiveUseBreakpoints="true"
          cloudName="dm99dkmlr"
          publicId="sample"
          style={{ width: '100%', height: '100%' }}>
          <Transformation effect="sepia" />
        </Image>
        In classic heading component
        <p data-id="text1" onDoubleClick={changeInfo}>{info.text1}</p>
        <p data-id="text2" onDoubleClick={changeInfo}>{info.text2}</p>
      </div>
    )
  }
}

ClassicHeading1.propTypes = {
  info: PropTypes.object,
  changeInfo: PropTypes.func,
  optionsBar: PropTypes.object,
  // children: PropTypes.object,
  code: PropTypes.string
}

export default ClassicHeading1
