import React, { Component } from 'react'
import OptionsBar from './OptionsBar'

// Consumer Wrapper
export const withOptionsBar = (Comp) => {
  return class WithOptionsBar extends Component {
    render () {
      return (
        <div>
          <OptionsBar/>
          <Comp {...this.props} />
        </div>
      )
    }
  }
}
