import React, { Component } from 'react'
import LinkEditable from './LinkEditable'

export default class LinksListEditable extends Component {
  showLinks = () => {
    const { mode, info, openChangeModal, linksStyle, componentStyle} = this.props
    const allLinks = []
    let i = 1
    for (const key in info) {
      const s = componentStyle[key] ? componentStyle[key] : {}
      if(info[key].type !== 'link') continue
      allLinks.push(<LinkEditable key={i} mode={mode} info={info[key]} style={linksStyle} data-id={key} onDoubleClick={openChangeModal} componentStyle={componentStyle}/>)
      i += 1      
    }
    return allLinks
  }

  render() {
    const { containerStyle } = this.props
    return (
      <div style={containerStyle}>
        { this.showLinks() }
      </div>
    );
  }

}
